import React, { useMemo } from 'react';
import { Sliders, Package, List } from 'lucide-react';
import { sampleDatasets } from './constants/datasets';
import { useCvrpAlgorithm } from './hooks/useCvrpAlgorithm';
import Visualization from './components/Visualization';
import Controls from './components/Controls';
import Summary from './components/Summary';
import Log from './components/Log';

export default function App() {
  const {
    state,
    selectedDataset,
    handlePlayPause,
    handleNextStep,
    handleReset,
    handleDatasetChange,
    handleSpeedChange
  } = useCvrpAlgorithm(Object.keys(sampleDatasets)[0]);

  const {
    depot,
    customerNodes,
    vehicles,
    currentVehicleId,
    logMessages,
    totalDistance,
    animationState,
    animationSpeed,
    algorithmState
  } = state;

  const allNodes = useMemo(() => [depot, ...customerNodes], [depot, customerNodes]);

  return (
    <div className="flex flex-col md:flex-row h-screen bg-gray-900 text-gray-100 font-sans">
      <main className="flex-1 flex flex-col p-4 md:p-8">
        <h1 className="text-3xl font-bold text-white mb-6 text-center">
          CVRP Hybrid Greedy Algorithm Visualizer
        </h1>
        <div className="flex-1 w-full max-w-4xl mx-auto h-64 md:h-auto">
          <Visualization
            nodes={allNodes}
            depot={depot}
            vehicles={vehicles}
            currentVehicleId={currentVehicleId}
          />
        </div>
      </main>

      <aside className="w-full md:w-96 lg:w-[450px] bg-gray-800 p-6 shadow-2xl flex flex-col space-y-6 overflow-y-auto">
        <div className="bg-gray-700/50 p-5 rounded-xl shadow-inner">
          <div className="flex items-center space-x-3 mb-4">
            <Sliders className="h-6 w-6 text-blue-400" />
            <h2 className="text-xl font-semibold text-white">Controls</h2>
          </div>
          <Controls
            datasets={sampleDatasets}
            selectedDataset={selectedDataset}
            onDatasetChange={handleDatasetChange}
            onPlayPause={handlePlayPause}
            onNextStep={handleNextStep}
            onReset={handleReset}
            onSpeedChange={handleSpeedChange}
            animationState={animationState}
            animationSpeed={animationSpeed}
            algorithmState={algorithmState}
          />
        </div>
        
        <div className="bg-gray-700/50 p-5 rounded-xl shadow-inner flex-shrink-0">
          <div className="flex items-center space-x-3 mb-4">
            <Package className="h-6 w-6 text-green-400" />
            <h2 className="text-xl font-semibold text-white">Results Summary</h2>
          </div>
          <Summary
            vehicles={vehicles}
            totalDistance={totalDistance}
          />
        </div>

        <div className="bg-gray-700/50 p-5 rounded-xl shadow-inner flex-1 flex flex-col min-h-[200px]">
          <div className="flex items-center space-x-3 mb-4">
            <List className="h-6 w-6 text-yellow-400" />
            <h2 className="text-xl font-semibold text-white">Step-by-Step Log</h2>
          </div>
          <Log messages={logMessages} />
        </div>
      </aside>
    </div>
  );
}
