import React from 'react';
import { Play, Pause, FastForward, RotateCcw, ChevronDown } from 'lucide-react';

const Controls = ({
  datasets,
  selectedDataset,
  onDatasetChange,
  onPlayPause,
  onNextStep,
  onReset,
  onSpeedChange,
  animationState,
  animationSpeed,
  algorithmState,
}) => {
  const isRunning = animationState === 'playing';
  const isDone = algorithmState === 'done';
  const isIdle = algorithmState === 'idle';

  return (
    <div className="space-y-6">
      <div>
        <label htmlFor="dataset" className="block text-sm font-medium text-gray-300 mb-1">
          Select Dataset
        </label>
        <div className="relative">
          <select
            id="dataset"
            value={selectedDataset}
            onChange={(e) => onDatasetChange(e.target.value)}
            disabled={!isIdle}
            className="w-full pl-3 pr-10 py-2 text-base bg-gray-700 border-gray-600 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm text-white appearance-none"
          >
            {Object.keys(datasets).map(name => (
              <option key={name} value={name}>{name}</option>
            ))}
          </select>
          <ChevronDown className="h-5 w-5 text-gray-400 absolute right-3 top-2.5 pointer-events-none" />
        </div>
      </div>

      <div className="grid grid-cols-3 gap-3">
        <button
          onClick={onPlayPause}
          disabled={isDone}
          className={`flex items-center justify-center p-3 rounded-lg text-white font-semibold shadow-md transition-all duration-200
            ${isDone ? 'bg-gray-600 cursor-not-allowed' : 
            (isRunning ? 'bg-yellow-600 hover:bg-yellow-700' : 'bg-blue-600 hover:bg-blue-700')}
          `}
        >
          {isRunning ? <Pause className="h-5 w-5" /> : <Play className="h-5 w-5" />}
          <span className="ml-2">{isRunning ? 'Pause' : 'Play'}</span>
        </button>

        <button
          onClick={onNextStep}
          disabled={isRunning || isDone}
          className="flex items-center justify-center p-3 rounded-lg text-white font-semibold shadow-md transition-all duration-200 bg-gray-600 hover:bg-gray-700 disabled:bg-gray-500 disabled:cursor-not-allowed"
        >
          <FastForward className="h-5 w-5" />
          <span className="ml-2">Next Step</span>
        </button>

        <button
          onClick={onReset}
          className="flex items-center justify-center p-3 rounded-lg text-white font-semibold shadow-md transition-all duration-200 bg-red-600 hover:bg-red-700"
        >
          <RotateCcw className="h-5 w-5" />
          <span className="ml-2">Reset</span>
        </button>
      </div>

      <div>
        <label htmlFor="speed" className="block text-sm font-medium text-gray-300 mb-2">
          Animation Speed
        </label>
        <div className="flex items-center space-x-3">
          <span className="text-xs text-gray-400">Fast</span>
          <input
            id="speed"
            type="range"
            min="50"
            max="1000"
            step="50"
            value={1050 - animationSpeed} // Invert slider
            onChange={(e) => onSpeedChange(1050 - parseInt(e.target.value, 10))}
            className="w-full h-2 bg-gray-700 rounded-lg appearance-none cursor-pointer range-sm"
          />
          <span className="text-xs text-gray-400">Slow</span>
        </div>
      </div>
    </div>
  );
};

export default Controls;
