import React, { useEffect, useRef } from 'react';

const Log = ({ messages }) => {
  const logEndRef = useRef(null);

  useEffect(() => {
    logEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  return (
    <div className="space-y-3">
      <h3 className="text-lg font-semibold text-white">Step-by-Step Log</h3>
      <div className="h-60 bg-gray-900 rounded-lg p-3 overflow-y-auto font-mono text-xs text-gray-300 space-y-2">
        {messages.map((msg, index) => (
          <p key={index} className="animate-fadeIn">
            <span className="text-gray-500 mr-2">{index + 1}:</span>
            {msg}
          </p>
        ))}
        <div ref={logEndRef} />
      </div>
    </div>
  );
};

export default Log;
