import React from 'react';
import { calculateRouteDistance } from '../algorithms/geometry';

const Summary = ({ vehicles, totalDistance }) => {
  return (
    <div className="space-y-4">
      <div className="flex justify-between items-baseline">
        <h3 className="text-lg font-semibold text-white">Summary</h3>
        <span className="text-sm text-gray-400">
          {vehicles.length} {vehicles.length === 1 ? 'Vehicle' : 'Vehicles'}
        </span>
      </div>
      
      <div className="bg-gray-700 p-3 rounded-lg">
        <span className="text-sm text-gray-300">Total Distance:</span>
        <span className="ml-2 text-lg font-bold text-blue-400">
          {totalDistance.toFixed(2)}
        </span>
      </div>
      
      <div className="space-y-3 max-h-40 overflow-y-auto pr-2">
        {vehicles.map(v => (
          <div key={v.id} className="bg-gray-700 p-3 rounded-lg">
            <div className="flex items-center justify-between mb-2">
              <span className="font-semibold text-white" style={{ color: v.color }}>
                Vehicle {v.id}
              </span>
              <span className="text-xs text-gray-400">
                Dist: {calculateRouteDistance(v.route).toFixed(1)} | 
                Load: {v.route.reduce((acc, n) => acc + n.demand, 0)} / {v.initialCapacity}
              </span>
            </div>
            <p className="text-xs text-gray-300 break-words">
              {v.route.map(n => n.id === v.route[0].id ? 'Depot' : `C${n.id}`).join(' → ')}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Summary;
